mapper.update:
UPDATE ProductPage
SET BoardTitle = #{boardTitle},
BoardBody = #{boardBody}
WHERE
id = #{id};

mapper.delete:
DELETE FROM File
WHERE boardId = #{id}
AND fileName =
#{fileName}

mapper.update:
UPDATE Product
SET Stock = #{stock},
price = #{price}
WHERE id =#{productId};

mapper.select:
SELECT Orders.orderId, Orders.memberId, Product.id as Productid, Orders.orderState, ProductPage.id as ProductPageid
FROM Orders Join Product ON Orders.productName = Product.name Join ProductPage ON Product.id = ProductPage.productId
WHERE orderState =1 and Orders.memberId=#{name} and ProductPage.id =#{id};
